import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import styles from './HomePage.module.css';
import styles1 from '../cliente/atendimentos/AtendimentosList.module.css';

const HomePage = () => {
  const [atendimentos, setAtendimentos] = useState([]);
  const [horariosDisponiveis, setHorariosDisponiveis] = useState([]);
  const [barbeiroId, setBarbeiroId] = useState(1); // Substitua pelo ID real do barbeiro logado

  useEffect(() => {
    const fetchAtendimentos = async () => {
      try {
        const response = await axios.get(`/home?barbeiroId=${barbeiroId}`);
        setAtendimentos(response.data);
      } catch (error) {
        console.error('Erro ao buscar atendimentos', error);
      }
    };

    const fetchHorarios = async () => {
      try {
        const response = await axios.get(`/home/horarios?barbeiroId=${barbeiroId}&data=${new Date().toISOString()}`);
        setHorariosDisponiveis(response.data);
      } catch (error) {
        console.error('Erro ao buscar horários', error);
      }
    };

    fetchAtendimentos();
    fetchHorarios();
  }, [barbeiroId]);

  return (
    <div className={styles.container}>
      <h1>Página Inicial</h1>
      
      <h2>Atendimentos</h2>
      <ul className={styles1.lisItem}>
        {atendimentos.map(atendimento => (
          <li key={atendimento.id}>{atendimento.nomeCliente} - {atendimento.precoTotalServicos} R$</li>
        ))}
      </ul>

      <h2>Horários Disponíveis</h2>
      <ul>
        {horariosDisponiveis.map((hora, index) => (
          <li key={index}>{hora}</li>
        ))}
      </ul>

      <Link to="/criar-horario">
        <button>Criar Novo Horário</button>
      </Link>

      <Link to="/atualizar-horario">
        <button>Atualizar Horário</button>
      </Link>

      <Link to="/servicos">
        <button>Ver Serviços</button>
      </Link>
    </div>
  );
};

export default HomePage;
