import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import BarbeiroSelector from './selectors/BarbeiroSelector';
import ServicoSelector from '.';
import HorarioSelector from '../createAtend/selectors/HorarioSelector';
import styles from './AtualizarAtendimento.module.css';

const UpdateAtendimento = () => {
  const { id } = useParams();
  const [barbeiros, setBarbeiros] = useState([]);
  const [selectedBarbeiroId, setSelectedBarbeiroId] = useState(null);
  const [servicos, setServicos] = useState([]);
  const [selectedServicos, setSelectedServicos] = useState([]);
  const [horarios, setHorarios] = useState([]);
  const [selectedHorario, setSelectedHorario] = useState('');

  useEffect(() => {
    axios.get(`/atendimento/${id}`)
      .then(response => {
        const data = response.data;
        setSelectedBarbeiroId(data.barbeiroId);
        setSelectedServicos(data.servicos);
        setSelectedHorario(data.horario);
        // Carregar barbeiros e serviços de acordo com o atendimento
        fetchBarbeiros();
        fetchServicos(data.barbeiroId);
        fetchHorarios(data.barbeiroId, data.servicos);
      })
      .catch(error => console.error('Erro ao buscar atendimento:', error));
  }, [id]);

  useEffect(() => {
    if (selectedBarbeiroId) {
      fetchServicos(selectedBarbeiroId);
    }
  }, [selectedBarbeiroId]);

  useEffect(() => {
    if (selectedBarbeiroId && selectedServicos.length > 0) {
      fetchHorarios(selectedBarbeiroId, selectedServicos);
    }
  }, [selectedBarbeiroId, selectedServicos]);

  const fetchBarbeiros = () => {
    axios.get('/barbeiros')
      .then(response => setBarbeiros(response.data))
      .catch(error => console.error('Erro ao buscar barbeiros:', error));
  };

  const fetchServicos = (barbeiroId) => {
    axios.get(`/servicos/${barbeiroId}`)
      .then(response => setServicos(response.data))
      .catch(error => console.error('Erro ao buscar serviços:', error));
  };

  const fetchHorarios = (barbeiroId, servicos) => {
    axios.get(`/horarios/${barbeiroId}?servicos=${servicos.join(',')}`)
      .then(response => setHorarios(response.data))
      .catch(error => console.error('Erro ao buscar horários:', error));
  };

  const handleSubmit = () => {
    axios.put('/atendimento/atualizar', {
      id,
      barbeiroId: selectedBarbeiroId,
      servicos: selectedServicos,
      horario: selectedHorario
    })
      .then(response => alert('Atendimento atualizado com sucesso'))
      .catch(error => console.error('Erro ao atualizar atendimento:', error));
  };

  return (
    <div className={styles.container}>
      <h2>Atualizar Atendimento</h2>
      <div className={styles.selectorContainer}>
        <BarbeiroSelector
          barbeiros={barbeiros}
          selectedBarbeiroId={selectedBarbeiroId}
          onSelect={setSelectedBarbeiroId}
        />
      </div>
      {selectedBarbeiroId && (
        <div className={styles.selectorContainer}>
          <ServicoSelector
            servicos={servicos}
            selectedServicos={selectedServicos}
            onSelect={setSelectedServicos}
          />
        </div>
      )}
      {selectedServicos.length > 0 && (
        <div className={styles.selectorContainer}>
          <HorarioSelector
            horarios={horarios}
            selectedHorario={selectedHorario}
            onSelect={setSelectedHorario}
          />
        </div>
      )}
      {selectedBarbeiroId && selectedServicos.length > 0 && selectedHorario && (
        <button className={styles.button} onClick={handleSubmit}>
          Atualizar Atendimento
        </button>
      )}
    </div>
  );
};

export default UpdateAtendimento;
