import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Modal from './Modal';
import styles from './AtualizarAtendimento.module.css';

const UpdateAtendimento = () => {
    const [horario, setHorario] = useState('');
    const [isModalOpen, setIsModalOpen] = useState(true);

    useEffect(() => {
        // Simular uma busca por atendimento
        axios.get('/atendimento/1') // Ajuste a URL conforme necessário
            .then(response => {
                setHorario(response.data.horario);
            })
            .catch(error => console.error('Erro ao buscar atendimento:', error));
    }, []);

    const handleSubmit = () => {
        axios.put('/atendimento/atualizar', {
            id: 1, // Ajuste conforme necessário
            horario
        })
            .then(response => {
                alert('Atendimento atualizado com sucesso');
                setIsModalOpen(false);
            })
            .catch(error => console.error('Erro ao atualizar atendimento:', error));
    };

    const handleClose = () => {
        setIsModalOpen(false);
    };

    return (
        <div>
            <button onClick={() => setIsModalOpen(true)}>Abrir Modal</button>
            <Modal isOpen={isModalOpen} onClose={handleClose}>
                <h2>Atualizar Atendimento</h2>
                <div className={styles.formContainer}>
                    <label>Horário:</label>
                    <input
                        type="datetime-local"
                        value={horario}
                        onChange={(e) => setHorario(e.target.value)}
                    />
                    <button onClick={handleSubmit}>Atualizar Atendimento</button>
                </div>
            </Modal>
        </div>
    );
};

export default UpdateAtendimento;
