// src/components/AtualizarAtendimento.jsx
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import Modal from './Modal';
import styles from './AtualizarAtendimento.module.css';

const AtendimentoList = ({ atendimentos }) => {
    const [selectedAtendimento, setSelectedAtendimento] = useState(null);
    const [isModalOpen, setIsModalOpen] = useState(false);
  
    const handleOpenModal = (atendimento) => {
      setSelectedAtendimento(atendimento);
      setIsModalOpen(true);
    };
  
    const handleCloseModal = () => {
      setIsModalOpen(false);
      setSelectedAtendimento(null);
    };
  
    return (
      <div className={styles.container}>
        <div className={styles.main}>
          <p>Meus</p>
          <h1>Atendimentos</h1>
          <div className={styles.box}>
            <ul>
              {atendimentos.map(atendimento => (
                <li key={atendimento.id} className={styles.listItem}>
                  {atendimento.barbeiroNome} - {atendimento.horario}
                  <button onClick={() => handleOpenModal(atendimento)}>
                    Atualizar
                  </button>
                </li>
              ))}
            </ul>
          </div>
          <Modal isOpen={isModalOpen} onClose={handleCloseModal}>
            {selectedAtendimento && (
              <div className={styles.formContainer}>
                <h2>Atualizar Atendimento</h2>
                <label>Horário:</label>
                <input
                  type="datetime-local"
                  value={selectedAtendimento.horario}
                  onChange={(e) => setSelectedAtendimento(prev => ({ ...prev, horario: e.target.value }))}
                />
                <button onClick={() => {
                  // Lógica de atualização aqui
                  handleCloseModal();
                }}>Atualizar Atendimento</button>
              </div>
            )}
          </Modal>
        </div>
        <Link to="/criar-atendimento">
          <button>Criar Novo Atendimento</button>
        </Link>
      </div>
    );
  };
export default UpdateAtendimento;
