const CriarHorarioPage = () => {
  const [diasTrabalho, setDiasTrabalho] = useState([]);
  const [horaInicio, setHoraInicio] = useState('');
  const [horaFim, setHoraFim] = useState('');
  const [horaInicioAlmoco, setHoraInicioAlmoco] = useState('');
  const [horaFimAlmoco, setHoraFimAlmoco] = useState('');
  const [barbeiroId, setBarbeiroId] = useState(1); // Substitua pelo ID real do barbeiro logado
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/home/novo-horario', {
        barbeiroId,
        diasTrabalho,
        horaInicio,
        horaFim,
        horaInicioAlmoco,
        horaFimAlmoco,
      });
      navigate('/home');
    } catch (error) {
      console.error('Erro ao criar horário', error);
    }
  };

  const diasSemana = [
    'Segunda-feira',
    'Terça-feira',
    'Quarta-feira',
    'Quinta-feira',
    'Sexta-feira',
    'Sábado',
    'Domingo'
  ];

  return (
    <div className="container">
      <h1>Criar Novo Horário</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Dias da Semana:
          <select
            multiple
            onChange={(e) => setDiasTrabalho([...e.target.selectedOptions].map(option => option.value))}
            value={diasTrabalho}
          >
            {diasSemana.map(dia => (
              <option key={dia} value={dia}>{dia}</option>
            ))}
          </select>
        </label>
        <label>
          Hora Início:
          <input type="time" onChange={(e) => setHoraInicio(e.target.value)} value={horaInicio} />
        </label>
        <label>
          Hora Fim:
          <input type="time" onChange={(e) => setHoraFim(e.target.value)} value={horaFim} />
        </label>
        <label>
          Hora Início Almoço:
          <input type="time" onChange={(e) => setHoraInicioAlmoco(e.target.value)} value={horaInicioAlmoco} />
        </label>
        <label>
          Hora Fim Almoço:
          <input type="time" onChange={(e) => setHoraFimAlmoco(e.target.value)} value={horaFimAlmoco} />
        </label>
        <button type="submit">Salvar</button>
      </form>
    </div>
  );
};

export default CriarHorarioPage;
