import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import styles from './CreateHorarios.module.css'; // Certifique-se de importar o CSS correto

const CriarHorarioPage = () => {
  const [diasTrabalho, setDiasTrabalho] = useState([]);
  const [horaInicio, setHoraInicio] = useState('');
  const [horaFim, setHoraFim] = useState('');
  const [horaInicioAlmoco, setHoraInicioAlmoco] = useState('');
  const [horaFimAlmoco, setHoraFimAlmoco] = useState('');
  const [barbeiroId, setBarbeiroId] = useState(1); // Substitua pelo ID real do barbeiro logado
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/home/novo-horario', {
        barbeiroId,
        diasTrabalho,
        horaInicio,
        horaFim,
        horaInicioAlmoco,
        horaFimAlmoco,
      });
      navigate('/home');
    } catch (error) {
      console.error('Erro ao criar horário', error);
    }
  };

  return (
    <div className={styles.container}>
      <h1>Criar Novo Horário</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Dias de Trabalho:
          <input type="text" onChange={(e) => setDiasTrabalho(e.target.value.split(','))} />
        </label>
        <label>
          Hora Início:
          <input type="time" onChange={(e) => setHoraInicio(e.target.value)} />
        </label>
        <label>
          Hora Fim:
          <input type="time" onChange={(e) => setHoraFim(e.target.value)} />
        </label>
        <label>
          Hora Início Almoço:
          <input type="time" onChange={(e) => setHoraInicioAlmoco(e.target.value)} />
        </label>
        <label>
          Hora Fim Almoço:
          <input type="time" onChange={(e) => setHoraFimAlmoco(e.target.value)} />
        </label>
        <button type="submit">Salvar</button>
      </form>
    </div>
  );
};

export default CriarHorarioPage;
