import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom'; // Adicione useParams aqui
import styles from './CancelarAtendimento.module.css'; 

const CancelarAtendimento = () => {
    const [showConfirmation, setShowConfirmation] = useState(false);
    const navigate = useNavigate();
    const { id } = useParams(); // Adicione esta linha

    const handleCancel = () => {
        // Simulação da lógica de cancelamento
        console.log(`Atendimento ${id} cancelado com sucesso`);

        // Simulação de redirecionamento
        navigate('/atendimento');
    };

    return (
        <div className={styles.container}>
            {showConfirmation ? (
                <div className={styles.confirmationPopup}>
                    <p>Você tem certeza que deseja cancelar este atendimento?</p>
                    <div
    style={{
        backgroundColor: 'rgb(172, 68, 68)',
        padding: '20px',
        borderRadius: '8px',
        boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
        textAlign: 'center',
        width: '400px',
        maxWidth: '80%',
        height: 'auto',
        maxHeight: '80vh',
        overflow: 'auto'
    }}
>
    <p>Você tem certeza?</p>
    <button style={{ padding: '10px 20px', border: 'none', borderRadius: '4px', backgroundColor: '#6200eb', color: 'white' }}>Sim</button>
    <button style={{ padding: '10px 20px', border: 'none', borderRadius: '4px', backgroundColor: '#ad2806', color: 'white' }}>Não</button>
</div>
                    <button
                        className={styles.confirmButton}
                        onClick={() => {
                            handleCancel();
                            setShowConfirmation(false);
                        }}
                    >
                        Sim
                    </button>
                    <button
                        className={styles.cancelButton}
                        onClick={() => setShowConfirmation(false)}
                    >
                        Não
                    </button>
                </div>
            ) : (
                <div className={styles.buttonContainer}>
                    <button
                        className={styles.buttonDelete}
                        onClick={() => setShowConfirmation(true)}
                    >
                        Cancelar
                    </button>
                </div>
            )}
        </div>
    );
};

export default CancelarAtendimento;
