import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import styles from './AtualizarAtendimento.module.css';
import BarbeiroSelector from '../createAtend/selectors/BarbeiroSelector';
import ServicoSelector from '../createAtend/selectors/ServicoSelector';
import HorarioSelector from '../createAtend/selectors/HorarioSelector';

const UpdateAtendimento = () => {
    const { id } = useParams();
    const [barbeiros, setBarbeiros] = useState([]);
    const [selectedBarbeiroId, setSelectedBarbeiroId] = useState(null);
    const [servicos, setServicos] = useState([]);
    const [selectedServicos, setSelectedServicos] = useState([]);
    const [horarios, setHorarios] = useState([]);
    const [selectedHorario, setSelectedHorario] = useState('');

    useEffect(() => {
        // Simular carregamento de dados
        const fetchData = async () => {
            try {
                const response = await axios.get(`/atendimento/${id}`);
                const atendimento = response.data;

                setSelectedBarbeiroId(atendimento.barbeiroId);
                setSelectedServicos(atendimento.servicos);
                setSelectedHorario(atendimento.horario);

                // Buscar barbeiros, serviços e horários disponíveis
                // Simulação para barbeiros
                setBarbeiros([
                    { id: 1, nomeBarbeiro: 'João' },
                    { id: 2, nomeBarbeiro: 'Pedro' },
                    { id: 3, nomeBarbeiro: 'Lucas' }
                ]);

                // Simulação para serviços
                setServicos([
                    { id: 1, nomeServico: 'Corte de Cabelo' },
                    { id: 2, nomeServico: 'Barba' },
                    { id: 3, nomeServico: 'Corte + Barba' }
                ]);

                // Simulação para horários
                setHorarios(['09:00', '10:00', '11:00', '14:00', '15:00']);
            } catch (error) {
                console.error('Erro ao buscar atendimento:', error);
            }
        };

        fetchData();
    }, [id]);

    const handleSubmit = () => {
        // Simulação de envio dos dados
        console.log('Atendimento atualizado com sucesso!');
    };

    return (
        <div className={styles.container}>
            <h2>Atualizar Atendimento</h2>
            <div className={styles.selectorContainer}>
                <BarbeiroSelector 
                    barbeiros={barbeiros} 
                    selectedBarbeiroId={selectedBarbeiroId} 
                    onSelect={setSelectedBarbeiroId} 
                />
            </div>
            <div className={styles.selectorContainer}>
                <ServicoSelector 
                    servicos={servicos} 
                    selectedServicos={selectedServicos} 
                    onSelect={setSelectedServicos} 
                />
            </div>
            <div className={styles.selectorContainer}>
                <HorarioSelector 
                    horarios={horarios} 
                    selectedHorario={selectedHorario} 
                    onSelect={setSelectedHorario} 
                />
            </div>
            {selectedBarbeiroId && selectedServicos.length > 0 && selectedHorario && (
                <button className={styles.button} onClick={handleSubmit}>Atualizar Atendimento</button>
            )}
        </div>
    );
};

export default UpdateAtendimento;
