        <div className={styles.box}>
          {/* Aqui você pode adicionar qualquer conteúdo adicional relacionado aos horários *//*}
        </div>
      </div>
      
      <button className={styles.button} onClick={handleCreateAtendimento}>Criar Agendamento</button>
