// src/components/atendimentos/createAtend/CreateAtendimento.jsx
import React, { useState, useEffect } from 'react';
import BarbeiroSelector from './selectors/BarbeiroSelector';
import ServicoSelector from './selectors/ServicoSelector';
import HorarioSelector from './selectors/HorarioSelector';
import styles from './CreateAtendimento.module.css';

const CreateAtendimento = ({ modo, atendimento }) => {
  const [barbeiros, setBarbeiros] = useState([]);
  const [selectedBarbeiroId, setSelectedBarbeiroId] = useState(modo === 'atualizar' ? atendimento.barbeiroId : null);
  const [servicos, setServicos] = useState([]);
  const [selectedServicos, setSelectedServicos] = useState(modo === 'atualizar' ? atendimento.servicos : []);
  const [horarios, setHorarios] = useState([]);
  const [selectedHorario, setSelectedHorario] = useState(modo === 'atualizar' ? atendimento.horario : '');
  const clienteId = 1; // ID do cliente, pode vir do contexto ou props

  useEffect(() => {
    // Simular carregamento de barbeiros
    setBarbeiros([{ id: 1, nomeBarbeiro: 'João' }, { id: 2, nomeBarbeiro: 'Pedro' }, { id: 3, nomeBarbeiro: 'Lucas' }]);
  }, []);

  useEffect(() => {
    if (selectedBarbeiroId) {
      // Simular serviços disponíveis
      setServicos([{ id: 1, nomeServico: 'Corte de Cabelo' }, { id: 2, nomeServico: 'Barba' }, { id: 3, nomeServico: 'Corte + Barba' }]);
    }
  }, [selectedBarbeiroId]);

  useEffect(() => {
    if (selectedBarbeiroId && selectedServicos.length) {
      // Simular horários disponíveis
      setHorarios(['09:00', '10:00', '11:00', '14:00', '15:00']);
    }
  }, [selectedBarbeiroId, selectedServicos]);

  const handleCreateAtendimento = () => {
    const atendimentoData = {
      clienteId,
      barbeiroId: selectedBarbeiroId,
      servicos: selectedServicos,
      horario: selectedHorario
    };

    if (modo === 'atualizar') {
      // Atualizar atendimento
      console.log("Atendimento atualizado com sucesso!", atendimentoData);
    } else {
      // Criar novo atendimento
      console.log("Atendimento criado com sucesso!", atendimentoData);
    }
  };

  const isButtonDisabled = modo === 'atualizar'
    ? !(selectedBarbeiroId && selectedServicos.length && selectedHorario)
    : !(selectedBarbeiroId && selectedServicos.length && selectedHorario);

  return (
    <div className={styles.container}>
      <p className={styles.title}>{modo === 'atualizar' ? 'Atualizar Atendimento' : 'Criar Atendimento'}</p>
      
      <div className={styles.selectorContainer}>
        <BarbeiroSelector 
          barbeiros={barbeiros} 
          selectedBarbeiroId={selectedBarbeiroId} 
          onSelect={setSelectedBarbeiroId} 
        />
      </div>
      
      <div className={styles.selectorContainer}>
        <ServicoSelector 
          servicos={servicos} 
          selectedServicos={selectedServicos} 
          onSelect={setSelectedServicos} 
        />
      </div>
      
      <div className={styles.selectorContainer}>
        <HorarioSelector 
          horarios={horarios} 
          selectedHorario={selectedHorario} 
          onSelect={setSelectedHorario} 
        />
      </div>
      
      <button
        className={styles.button}
        onClick={handleCreateAtendimento}
        disabled={isButtonDisabled}
      >
        {modo === 'atualizar' ? 'Atualizar Agendamento' : 'Criar Agendamento'}
      </button>
    </div>
  );
};

export default CreateAtendimento;
