import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './AtualizarHorariospage.module.css'; // Certifique-se de que o caminho está correto

const AtualizarHorarioPage = () => {
  const [horario, setHorario] = useState({});
  const [data, setData] = useState('');
  const [horaInicio, setHoraInicio] = useState('');
  const [horaFim, setHoraFim] = useState('');
  const [horaInicioAlmoco, setHoraInicioAlmoco] = useState('');
  const [horaFimAlmoco, setHoraFimAlmoco] = useState('');
  const [barbeiroId, setBarbeiroId] = useState(1); // Substitua pelo ID real do barbeiro logado
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch existing horario data if needed
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`/home/atualizar-horario?barbeiroId=${barbeiroId}&data=${data}`, {
        horaInicio,
        horaFim,
        horaInicioAlmoco,
        horaFimAlmoco,
      });
      navigate('/home');
    } catch (error) {
      console.error('Erro ao atualizar horário', error);
    }
  };

  return (
    <div className="container">
      <h1>Atualizar Horário</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Data:
          <input type="date" onChange={(e) => setData(e.target.value)} />
        </label>
        <label>
          Hora Início:
          <input type="time" onChange={(e) => setHoraInicio(e.target.value)} />
        </label>
        <label>
          Hora Fim:
          <input type="time" onChange={(e) => setHoraFim(e.target.value)} />
        </label>
        <label>
          Hora Início Almoço:
          <input type="time" onChange={(e) => setHoraInicioAlmoco(e.target.value)} />
        </label>
        <label>
          Hora Fim Almoço:
          <input type="time" onChange={(e) => setHoraFimAlmoco(e.target.value)} />
        </label>
        <button type="submit">Salvar</button>
      </form>
    </div>
  );
};

export default AtualizarHorarioPage;
