const AtendimentoList = () => {
  const [atendimentos, setAtendimentos] = useState([]);

  useEffect(() => {
    // Simular um atendimento fictício para testes com todos os dados necessários
    setAtendimentos([
      {
        id: 1,
        clienteNome: 'Carlos',
        barbeiroNome: 'João',
        servicos: ['Corte de cabelo', 'Barba'],
        horario: '2024-08-10T10:00',
        valor: 50.00,
      },
      {
        id: 2,
        clienteNome: 'Ana',
        barbeiroNome: 'Maria',
        servicos: ['Tintura', 'Corte de cabelo'],
        horario: '2024-08-11T11:00',
        valor: 80.00,
      }
    ]);
  }, []);

  return (
    <div className={styles.container}>
      <div className={styles.main}>
        <p>Meus</p>
        <h1>Atendimentos</h1>
        <div className={styles.box}>
          <ul>
            {atendimentos.map(atendimento => (
              <li key={atendimento.id} className={styles.listItem}>
                <div className={styles.details}>
                <div className={styles.barbeiroNome}>
  <span className={styles.label}>Barbeiro:</span> 
  <span className={styles.nome}>{atendimento.barbeiroNome}</span>
</div>
                  <div className={styles.horario}>
                    Horário: {atendimento.horario}
                  </div>
                  <div className={styles.servicos}>
                    Serviços: {atendimento.servicos.join(', ')}
                  </div>
                  <div className={styles.valor}>
                    Valor: R$ {atendimento.valor.toFixed(2)}
                  </div>
                </div>
                <div className={styles.buttonContainer}>
                  <Link to={`/atualizar-atendimento/${atendimento.id}`}>
                    <button className={styles.buttonUpdate}>
                      Atualizar
                    </button>
                  </Link>
                  <Link to={`/cancelar-atendimento/${atendimento.id}`}>
                    <button className={styles.buttonDelete}>
                      Cancelar
                    </button>
                  </Link>
                </div>
              </li>
            ))}
          </ul>
        </div>
      </div>
      <div>
        <Link to="/criar-atendimento">
          <div className={styles.buttonCreate}>
            <button
              onMouseDown={(e) => e.preventDefault()}
              onFocus={(e) => e.target.style.outline = 'none'}
            >
              Criar Novo Atendimento
            </button>
          </div>
        </Link>
      </div>
    </div>

  );
};

export default AtendimentoList;
