import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import styles from '../AtendimentosList.module.css';

const CancelarAtendimento = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [showConfirmation, setShowConfirmation] = useState(true);

  const handleCancel = () => {
    axios.delete(`/atendimento/cancelar?id=${id}`)
      .then(response => {
        alert(`Atendimento ${id} cancelado com sucesso`);
        navigate('/atendimento');
      })
      .catch(error => {
        console.error('Erro ao cancelar atendimento:', error);
        setShowConfirmation(false); // Fechar o pop-up em caso de erro
      });
  };

  return (
    <div className={styles.container}>
      {showConfirmation ? (
        <div className={styles.confirmPopup}>
          <p>Você tem certeza que deseja cancelar este atendimento?</p>
          <button onClick={handleCancel}>Sim</button>
          <button onClick={() => navigate('/atendimento')}>Não</button>
        </div>
      ) : (
        <div className={styles.errorPopup}>
          <p>Não foi possível cancelar o atendimento.</p>
          <button onClick={() => navigate('/atendimento')}>Voltar</button>
        </div>
      )}
    </div>
  );
};

export default CancelarAtendimento;
