import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const ServicosPage = () => {
    const [servicos, setServicos] = useState([]);
    const [barbeiroId, setBarbeiroId] = useState(1); // Substitua pelo ID real do barbeiro logado
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
  
    useEffect(() => {
      const fetchServicos = async () => {
        try {
          const response = await axios.get(`/home/servicos?barbeiroId=${barbeiroId}`);
          setServicos(response.data);
        } catch (error) {
          setError('Erro ao buscar serviços');
          console.error('Erro ao buscar serviços', error);
        } finally {
          setLoading(false);
        }
      };
  
      fetchServicos();
    }, [barbeiroId]);
  
    const handleDelete = async (servicoId) => {
      if (window.confirm('Tem certeza que deseja excluir este serviço?')) {
        try {
          await axios.delete(`/home/servicos/excluir`, { data: { id: servicoId } });
          setServicos(servicos.filter(servico => servico.id !== servicoId));
        } catch (error) {
          setError('Erro ao excluir serviço');
          console.error('Erro ao excluir serviço', error);
        }
      }
    };
  
    return (
      <div>
        <h1>Serviços</h1>
        {loading ? (
          <p>Carregando...</p>
        ) : error ? (
          <p>{error}</p>
        ) : servicos.length === 0 ? (
          <p>Nenhum serviço encontrado.</p>
        ) : (
          <ul>
            {servicos.map(servico => (
              <li key={servico.id}>
                {servico.nomeServico} - {servico.preco} R$
                <button onClick={() => handleDelete(servico.id)}>Excluir</button>
              </li>
            ))}
          </ul>
        )}
  
        <Link to="/criar-servico">
          <button>Criar Novo Serviço</button>
        </Link>
      </div>
    );
  };
  
  export default ServicosPage;
