// src/components/CreateClientForm.js
<style>
@import url('https://fonts.googleapis.com/css2?family=Open+Sans:ital,wght@0,300..800;1,300..800&display=swap');
</style>
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './RegistrarCliente.css'

const CreateClientForm = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [phone, setPhone] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (event) => {
    event.preventDefault();
    
    try {
      const response = await axios.post('/cliente/criar', {
        name,
        email,
        phone,
      });

      // Redirecionar para a página de atendimentos com o clienteId
      const clienteId = new URL(response.headers.location).searchParams.get('clienteId');
      navigate(`/atendimento?clienteId=${clienteId}`);
    } catch (error) {
      console.error('Erro ao criar cliente:', error);
    }
  };

  return (
    <div className='main'>
     <p className='h1'>Registrar Cliente</p>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Nome:</label>
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Email:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Telefone:</label>
          <input
            type="tel"
            value={phone}
            onChange={(e) => setPhone(e.target.value)}
            required
          />
        </div>
        <button type="submit">Criar Cliente</button>
      </form>
    </div>
  );
};

export default CreateClientForm;
