// src/components/atendimentos/cancelar/CancelarAtendimento.js
/*import React, { useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';

const CancelarAtendimento = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const query = new URLSearchParams(location.search);
  const id = query.get('id');

  useEffect(() => {
    if (id) {
      axios.delete(`/atendimento/cancelar?id=${id}`)
        .then(response => {
          alert('Atendimento cancelado com sucesso');
          navigate('/atendimento'); // Redireciona para a lista de atendimentos
        })
        .catch(error => console.error('Erro ao cancelar atendimento:', error));
    }
  }, [id, navigate]);

  return (
    <div>
      <p>Cancelando atendimento...</p>
    </div>
  );
};

export default CancelarAtendimento;*/

const CancelarAtendimento = () => {
    const [showConfirmation, setShowConfirmation] = useState(false);
    const navigate = useNavigate();
    const { id } = useParams();

    const handleCancel = () => {
        // Simulação da lógica de cancelamento
        console.log(`Atendimento ${id} cancelado com sucesso`);

        // Simulação de redirecionamento
        navigate('/atendimento');
    };

    return (
        <div className={styles.container}>
            {showConfirmation ? (
                <div className={styles.confirmationPopup}>
                    <p>Você tem certeza que deseja cancelar este atendimento?</p>
                    <button
                        className={styles.confirmButton}
                        onClick={() => {
                            handleCancel();
                            setShowConfirmation(false);
                        }}
                    >
                        Sim
                    </button>
                    <button
                        className={styles.cancelButton}
                        onClick={() => setShowConfirmation(false)}
                    >
                        Não
                    </button>
                </div>
            ) : (
                <div className={styles.buttonContainer}>
                    <button
                        className={styles.buttonDelete}
                        onClick={() => setShowConfirmation(true)}
                    >
                        Cancelar
                    </button>
                </div>
            )}
        </div>
    );
};

export default CancelarAtendimento;
