import React, { useState } from 'react';
import Modal from './update/'; // Caminho para o Modal
import styles from './AtendimentosList.module.css';
const AtendimentoList = () => {
  const [atendimentos, setAtendimentos] = useState([]);
  const [selectedAtendimento, setSelectedAtendimento] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  useEffect(() => {
    axios.get('/atendimentos') // Ajuste a URL conforme necessário
      .then(response => {
        setAtendimentos(response.data);
      })
      .catch(error => console.error('Erro ao buscar atendimentos:', error));
  }, []);

  const handleOpenModal = (atendimento) => {
    setSelectedAtendimento(atendimento);
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
    setSelectedAtendimento(null);
  };

  return (
    <div className={styles.container}>
      <h1>Lista de Atendimentos</h1>
      <ul>
        {atendimentos.map(atendimento => (
          <li key={atendimento.id} className={styles.listItem}>
            {atendimento.barbeiroNome} - {atendimento.horario}
            <button onClick={() => handleOpenModal(atendimento)}>
              Atualizar
            </button>
          </li>
        ))}
      </ul>
      <Modal isOpen={isModalOpen} onClose={handleCloseModal}>
        {selectedAtendimento && (
          <div className={styles.formContainer}>
            <h2>Atualizar Atendimento</h2>
            <label>Horário:</label>
            <input
              type="datetime-local"
              value={selectedAtendimento.horario}
              onChange={(e) => setSelectedAtendimento(prev => ({ ...prev, horario: e.target.value }))}
            />
            <button onClick={() => alert('Atendimento atualizado com sucesso')}>Atualizar Atendimento</button>
          </div>
        )}
      </Modal>
    </div>
  );
};

export default AtendimentoList;
